import React, { useState, useEffect } from 'react';
import CopyToClipboard from "react-copy-to-clipboard";
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const InputUrl = () => {
  const [url, setUrl] = useState('');
  const [shortenedId, setShortenedId] = useState('');
  const [copied, setCopied] = useState(false);
  const [redirectUrl, setRedirectUrl] = useState('');
  const history = useHistory();

  const handleInputChange = (event) => {
    setUrl(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      console.log('Submitting URL:', url);
      const response = await axios.post('http://localhost:5000/api/url/shorten', {
        redirectUrl: url
      });
      console.log('Shortened URL response:', response.data);
      setShortenedId(response.data.id);
      setRedirectUrl(`http://dr-ait/${response.data.id}`);
      history.push(`/result/${response.data.id}`); // Redirect after shortening
    } catch (error) {
      console.error('Error shortening URL:', error.message);
    }
  };

  const handleCopy = () => {
    setCopied(true);
  };

  useEffect(() => {
    if (copied) {
      const timer = setTimeout(() => {
        setCopied(false);
      }, 1000);

      return () => {
        clearTimeout(timer);
      };
    }
  }, [copied]);

  return (
    <div className='inputContainer'>
      <h1>URL<span>&nbsp;SHORTENER</span></h1>
      <form onSubmit={handleSubmit}>
        <input
          type='text'
          placeholder='Please provide your URL here'
          value={url}
          onChange={handleInputChange}
        />
        <button type='submit'>SHORTEN</button>
      </form>
      {redirectUrl && (
        <div className='result'>
          <p>Shortened Url: <a href={redirectUrl} target='_blank' rel='noopener noreferrer'>{redirectUrl}</a></p>
          <CopyToClipboard text={redirectUrl} onCopy={handleCopy}>
            <button className={copied ? "copied" : ""}>
              {copied ? "Copied!" : "Copy To Clipboard"}
            </button>
          </CopyToClipboard>
        </div>
      )}
    </div>
  );
};

export default InputUrl;
