import React, { useState } from 'react';
import axios from 'axios';

const InputUrl = ({ }) => {
  const [url, setUrl] = useState('');
  const [shortenedUrl, setShortenedUrl] = useState('');
  const handleInputChange = (event) => {
    setUrl(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      console.log('Submitting URL:', url);
      const response = await axios.post('http://localhost:5000/api/url/shorten',{
        redirectUrl: url
      });
      console.log('Shortened URL response:', response.data);
      setShortenedUrl(`http://localhost:5000/${response.data.id}`);
      console.log(shortenedUrl);
    } catch (error) {
      console.error('Error shortening URL:', error.message);
    }
  };

  // console.log('Rendered with URL:', url);

  // useEffect(() => {
  //   const fetch = async () => {
  //       console.log("BITBIDI");
  //       const response = await axios.get("http://localhost:5000/api/url/");
  //       console.log(response.data);
  //   }

  //   fetch();
  //   }, [])
  return (
    <div className='inputContainer'>
      <h1>URL<span>&nbsp;SHORTENER</span></h1>
      <form onSubmit={handleSubmit}>
        <input
          type='text'
          placeholder='Please provide your URL here'
          value={url}
          onChange={handleInputChange}
        />
        <button type='submit'>SHORTEN</button>
      </form>
    </div>
  );
};

export default InputUrl;
