import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const ResultLink = ({ shortenedId }) => {
  const [copied, setCopied] = useState(false);
  const [redirectUrl, setRedirectUrl] = useState('');
  const history = useHistory();

  const handleCopy = () => {
    setCopied(true);
  };

  const fetchRedirectUrl = async () => {
    try {
      console.log('Fetching redirect URL for:', shortenedId);
      setRedirectUrl(`http://dr-ait/${shortenedId}`);
    } catch (error) {
      console.error('Error fetching redirect URL:', error);
    }
  };

  useEffect(() => {
    fetchRedirectUrl();
  }, [shortenedId]);

  useEffect(() => {
    let timer;
    if (copied) {
      timer = setTimeout(() => {
        setCopied(false);
      }, 1000);
    }

    return () => {
      clearTimeout(timer);
    };
  }, [copied]);

  console.log('Rendering ResultLink with shortenedId:', shortenedId);

  return (
    <>
      <div className='result'>
        {redirectUrl ? (
          <>
            <p>Shortened Url: <a href={redirectUrl} target='_blank' rel='noopener noreferrer'>{redirectUrl}</a></p>
            <CopyToClipboard text={redirectUrl} onCopy={handleCopy}>
              <button className={copied ? "copied" : ""}>
                {copied ? "Copied!" : "Copy To Clipboard"}
              </button>
            </CopyToClipboard>
          </>
        ) : (
          <p>Loading...</p>
        )}
      </div>
    </>
  );
};

const InputUrl = () => {
  const [url, setUrl] = useState('');
  const [shortenedId, setShortenedId] = useState('');
  const history = useHistory();

  const handleInputChange = (event) => {
    setUrl(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      console.log('Submitting URL:', url);
      const response = await axios.post('http://localhost:5000/api/url/shorten', {
        redirectUrl: url
      });
      console.log('Shortened URL response:', response.data);
      setShortenedId(response.data.id);

      // Redirect to the shortened URL page
      history.push(`/dr-ait/${response.data.id}`);
    } catch (error) {
      console.error('Error shortening URL:', error.message);
    }
  };

  return (
    <div className='inputContainer'>
      <h1>URL<span>&nbsp;SHORTENER</span></h1>
      <form onSubmit={handleSubmit}>
        <input
          type='text'
          placeholder='Please provide your URL here'
          value={url}
          onChange={handleInputChange}
        />
        <button type='submit'>SHORTEN</button>
      </form>
      {shortenedId && <ResultLink shortenedId={shortenedId} />}
    </div>
  );
};

export default InputUrl;
