import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory, Link, useParams } from 'react-router-dom';
import CopyToClipboard from 'react-copy-to-clipboard';

const InputUrl = () => {
  const [url, setUrl] = useState('');
  const [shortenedUrl, setShortenedUrl] = useState('');
  const [copied, setCopied] = useState(false);
  const [redirectUrl, setRedirectUrl] = useState('');
  const history = useHistory();
  const { id } = useParams();

  const handleInputChange = (event) => {
    setUrl(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      console.log('Submitting URL:', url);
      const response = await axios.post('http://localhost:5000/api/url/shorten', {
        redirectUrl: url
      });
      console.log('Shortened URL response:', response.data);
      setShortenedUrl(response.data.id);
      history.push(`/result/${response.data.id}`);
    } catch (error) {
      console.error('Error shortening URL:', error.message);
    }
  };

  const handleCopy = () => {
    setCopied(true);
  };

  const fetchRedirectUrl = async () => {
    try {
      console.log('Fetching redirect URL for:', id);
      const response = await axios.get(`http://localhost:5000/api/url/${id}`);
      setRedirectUrl(response.data.redirectUrl);
    } catch (error) {
      console.error('Error fetching redirect URL:', error);
    }
  };

  useEffect(() => {
    if (id) {
      fetchRedirectUrl();
    }
  }, [id]);

  useEffect(() => {
    let timer;
    if (copied) {
      timer = setTimeout(() => {
        setCopied(false);
      }, 1000);
    }

    return () => {
      clearTimeout(timer);
    };
  }, [copied]);

  return (
    <div className='inputContainer'>
      <h1>URL<span>&nbsp;SHORTENER</span></h1>
      <form onSubmit={handleSubmit}>
        <input
          type='text'
          placeholder='Please provide your URL here'
          value={url}
          onChange={handleInputChange}
        />
        <button type='submit'>SHORTEN</button>
      </form>
      {shortenedUrl && (
        <div className='result'>
          <p>Shortened Url: <Link to={`/result/${shortenedUrl}`} target='_blank' rel='noopener noreferrer'>{`http://dr-ait/${shortenedUrl}`}</Link></p>
          <CopyToClipboard text={`http://dr-ait/${shortenedUrl}`} onCopy={handleCopy}>
            <button className={copied ? "copied" : ""}>
              {copied ? "Copied!" : "Copy To Clipboard"}
            </button>
          </CopyToClipboard>
        </div>
      )}
      {redirectUrl && (
        <div>
          Redirecting...
          <meta httpEquiv="refresh" content={`3;URL='${redirectUrl}'`} />
        </div>
      )}
    </div>
  );
};

export default InputUrl;
